element.calculation:
(function calculatedFieldValue(current) {

	// Deal Category:
	// < $20 mil, Category = C
	// < $149 mil, Category = B
	// >= $150 mil, Category = A
	// Zero, negative, or NaN, Category = <blank>
	rtn = '';
	var num = parseFloat(current.sf_total_contract_value);
	if (isNaN(num)) {
		rtn = '';
	} else if (num <= 0) {
		rtn = '';
	} else if (num <= 20000000) {
		rtn = 'C';
	} else if (num < 150000000) {
		rtn = 'B';
	} else {
		rtn = 'A';
	}
	return rtn;

})(current);
