sys_ui_page.client_script:
function enterForm(){
	var vals = jQuery('#case_templates').val();
	jQuery('#template_values').val(vals.toString());
	return true;
}

jQuery(document).ready(function () {
    jQuery('#case_templates').multiSelect({ keepOrder: true });
});

